import{_ as a,c as e,o as r,ae as d}from"./chunks/framework.B0V9E2-J.js";const s="/readme/assets/mobile_list_normal.vtz_-CC-.png",i="/readme/assets/mobile_list_detail.CCZi4rl3.png",l="/readme/assets/mobile_list_waterfall.tPQLEgGJ.png",_="/readme/assets/pc_list_normal.CaKc9AXb.png",o="/readme/assets/pc_list_detail.DfDcmySO.png",p="/readme/assets/pc_list_waterfall.Dih7xhlt.png",n="/readme/assets/preview_waterfall.BHXxWLHC.png",u=JSON.parse('{"title":"不明显的一些功能","description":"","frontmatter":{},"headers":[],"relativePath":"diary/不明显的一些功能.md","filePath":"diary/不明显的一些功能.md"}'),c={name:"diary/不明显的一些功能.md"};function m(b,t,h,k,f,g){return r(),e("div",null,t[0]||(t[0]=[d('<h1 id="不明显的一些功能" tabindex="-1">不明显的一些功能 <a class="header-anchor" href="#不明显的一些功能" aria-label="Permalink to &quot;不明显的一些功能&quot;">​</a></h1><p>除了明面上显示的一些功能之后，有些功能属于比较隐蔽，没有那么明显的。</p><h2 id="_1-列表模式切换" tabindex="-1">1. 列表模式切换 <a class="header-anchor" href="#_1-列表模式切换" aria-label="Permalink to &quot;1. 列表模式切换&quot;">​</a></h2><p>移动端主页只是一个单纯的标题列表，不能查看带详细内容的列表。</p><p>为了实现列表模式切换，我把切换功能添加到了 LOGO 中，移动端点击 LOGO 会在以下三种展示模式之间切换。</p><img width="200" src="'+s+'" alt="list-normal"><img width="200" src="'+i+'" alt="list-detail"><img width="200" src="'+l+'" alt="list-waterfall"><p>PC 端切换是直接点击列表状态按钮</p><p><img src="'+_+'" alt="pc_list_normal"><img src="'+o+'" alt="pc_list_detail"><img src="'+p+'" alt="pc_list_waterfall"></p><p>瀑布流显示是这样的：</p><p><img src="'+n+'" alt="preview_waterfall"></p><h2 id="_2-日记编辑的快捷键" tabindex="-1">2. 日记编辑的快捷键 <a class="header-anchor" href="#_2-日记编辑的快捷键" aria-label="Permalink to &quot;2. 日记编辑的快捷键&quot;">​</a></h2><p>日记在编辑状态时，PC 端可以使用快捷键 <strong>Ctrl + S</strong> 快捷保存日记。</p><p>在编写日记内容时</p><table tabindex="0"><thead><tr><th>快捷键</th><th>功能</th></tr></thead><tbody><tr><td><kbd>CTRL</kbd>+ <kbd>D </kbd></td><td>复制当前行。</td></tr><tr><td><kbd>Tab</kbd></td><td>在前面插入 4 个空格。</td></tr><tr><td><kbd>Shift</kbd> + <kbd>Tab</kbd></td><td>删除行前的空格，多于4个，一次性删除4个，小于四个，清除前面的空格。</td></tr><tr><td><kbd>CTRL</kbd>+ <kbd>← </kbd></td><td>移动到行最左端</td></tr><tr><td><kbd>CTRL</kbd>+ <kbd>→ </kbd></td><td>移动到行最右端</td></tr></tbody></table>',16)]))}const w=a(c,[["render",m]]);export{u as __pageData,w as default};
